import com.jpsite.rateLimiter.ExtRateLimiter;
import com.jpsite.upload.api.vo.PicUploadResult;
import com.jpsite.upload.service.PicUploadFileSystemService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;
import org.springframework.web.bind.annotation.RestController;
import org.springframework.web.multipart.MultipartFile;

/**
 * @author jiangpen
 * @date 2019/11/1510:17
 */
@RestController
@RequestMapping("pic/upload/fileSystem")
public class PicUploadFileSystemController {

    @Autowired
    private PicUploadFileSystemService picUploadFileSystemService;

    @PostMapping("/")
    @ResponseBody
    public PicUploadResult uploadLocal(@RequestParam("file") MultipartFile uploadFile) {
        return picUploadFileSystemService.upload(uploadFile);
    }

    @GetMapping("/index")
    @ExtRateLimiter(speed = 1, timeOut = 1000)
    public String rateLimit(){
        String a = "234";
        return "success";
    }
}
